import React, { useReducer, lazy, Suspense, useCallback } from 'react';
import PropTypes from 'prop-types';
import Login from './components/Login';
import TopicPage from './components/TopicPage';
import './styles/App.css';

// Lazy load FileUpload for performance (only loads after login/topic)
const FileUpload = lazy(() => import('./components/FileUpload'));

// Reducer for app state (efficiently manages login, topic, and errors)
const initialState = { isLoggedIn: false, showTopic: false, error: null };
function appReducer(state, action) {
  switch (action.type) {
    case 'LOGIN_SUCCESS':
      return { ...state, isLoggedIn: true, showTopic: true, error: null };
    case 'SHOW_UPLOAD':
      return { ...state, showTopic: false };
    case 'LOGOUT':
      return initialState;
    case 'SET_ERROR':
      return { ...state, error: action.payload };
    default:
      return state;
  }
}

// Global Error Boundary (catches React errors)
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true, error };
  }

  componentDidCatch(error, errorInfo) {
    console.error('App Error:', error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="error-boundary" role="alert">
          <h2>Something went wrong.</h2>
          <p>{this.state.error?.message || 'Unknown error'}</p>
          <button onClick={() => window.location.reload()}>Reload App</button>
        </div>
      );
    }
    return this.props.children;
  }
}

ErrorBoundary.propTypes = {
  children: PropTypes.node.isRequired,
};

function App() {
  const [state, dispatch] = useReducer(appReducer, initialState);

  const handleLoginSuccess = useCallback(() => {
    dispatch({ type: 'LOGIN_SUCCESS' });
  }, []);

  const handleShowUpload = useCallback(() => {
    dispatch({ type: 'SHOW_UPLOAD' });
  }, []);

  const handleLogout = useCallback(() => {
    dispatch({ type: 'LOGOUT' });
  }, []);

  const setError = useCallback((error) => {
    dispatch({ type: 'SET_ERROR', payload: error });
  }, []);

  return (
    <ErrorBoundary>
      <div className="App">
        {state.error && (
          <div className="global-error" role="alert">
            {state.error}
            <button onClick={() => dispatch({ type: 'SET_ERROR', payload: null })} aria-label="Dismiss error">Ã—</button>
          </div>
        )}
        {!state.isLoggedIn ? (
          <Login onLoginSuccess={handleLoginSuccess} setError={setError} />
        ) : state.showTopic ? (
          <TopicPage onContinue={handleShowUpload} onLogout={handleLogout} />
        ) : (
          <Suspense fallback={<div className="loading">Loading Upload Page...</div>}>
            <FileUpload onLogout={handleLogout} setError={setError} />
          </Suspense>
        )}
      </div>
    </ErrorBoundary>
  );
}

App.propTypes = {
  // No props for App
};

export default React.memo(App);  // Memoize to prevent re-renders
