import React, { useCallback } from 'react';
import PropTypes from 'prop-types';
import '../styles/App.css';  // Global styles (gradient, animations)

// Simple Loading Spinner (Reusable from App.css)
const LoadingSpinner = () => (
    <div className="loading" aria-label="Loading" />
);

function TopicPage({ onContinue, onLogout, setError }) {
    const handleContinue = useCallback(() => {
        try {
            onContinue();  // Navigate to FileUpload
        } catch (error) {
            setError('Failed to load upload page. Please try again.');
        }
    }, [onContinue, setError]);

    const handleLogout = useCallback(() => {
        try {
            onLogout();  // Back to login
        } catch (error) {
            setError('Logout failed. Reloading...');
            setTimeout(() => window.location.reload(), 1000);
        }
    }, [onLogout, setError]);

    return (
        <div className="topic-page animated-bg">
            {/* Navigation Bar (Matches App.css) */}
            <nav className="navbar" role="navigation" aria-label="Main navigation">
                <h1>File Protector</h1>
                <button onClick={handleLogout} aria-label="Logout">
                    Logout
                </button>
            </nav>

            {/* Main Content */}
            <main className="topic-content" role="main">
                <h2>Welcome to File Protector</h2>
                <p>
                    This secure web application demonstrates best practices for protecting sensitive files during upload.
                    Your files are encrypted, stored safely, and "cracked" using simulated brute-force techniques to show vulnerabilities.
                </p>
                <ul className="topic-features">
                    <li>üîí <strong>Secure Upload:</strong> Files are encrypted with AES before storage.</li>
                    <li>üõ°Ô∏è <strong>Password Protection:</strong> Admin login required (demo: admin/password123).</li>
                    <li>üíª <strong>Simulation:</strong> "Cracking" generates a demo key ‚Äì in reality, use strong passwords!</li>
                    <li>üìÅ <strong>Supported Files:</strong> ZIP, PDF, TXT, etc. (Max 10MB).</li>
                </ul>
                <button
                    onClick={handleContinue}
                    className="continue-button"
                    aria-label="Continue to file upload"
                >
                    Continue to Upload
                    <LoadingSpinner />  {/* Hidden spinner if needed in future */}
                </button>
            </main>
        </div>
    );
}

TopicPage.propTypes = {
    onContinue: PropTypes.func.isRequired,
    onLogout: PropTypes.func.isRequired,
    setError: PropTypes.func.isRequired,
};

export default React.memo(TopicPage);  // Memoize to prevent unnecessary re-renders
