import React, { useState, useCallback, useEffect, useRef } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import '../styles/Login.css';

// Custom Hook for Authentication API (Memoized for Efficiency)
function useAuthAPI() {
    const login = useCallback(async (credentials) => {
        try {
            const response = await axios.post('/api/auth/login', credentials, {
                timeout: 5000,  // 5s timeout to prevent hanging
            });
            return { success: true, data: response.data };
        } catch (error) {
            const message = error.response?.status === 401
                ? 'Invalid username or password.'
                : error.response?.data?.message || 'Login failed. Please check your connection.';
            return { success: false, error: message };
        }
    }, []);

    return { login };
}

// Simple Toast Component (Reusable, Auto-Dismiss)
const Toast = React.memo(({ message, type = 'error', onClose }) => {
    useEffect(() => {
        const timer = setTimeout(onClose, 3000);  // Auto-dismiss after 3s
        return () => clearTimeout(timer);
    }, [onClose]);

    return (
        <div className={`toast ${type}`} role="alert" aria-live="polite">
            {message}
            <button onClick={onClose} aria-label="Close notification">Ã—</button>
        </div>
    );
});

Toast.propTypes = {
    message: PropTypes.string.isRequired,
    type: PropTypes.oneOf(['success', 'error']),
    onClose: PropTypes.func.isRequired,
};

function Login({ onLoginSuccess, setError }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [showToast, setShowToast] = useState(false);
    const [toastMessage, setToastMessage] = useState('');
    const [toastType, setToastType] = useState('error');
    const [validation, setValidation] = useState({ username: '', password: '' });

    const usernameRef = useRef(null);
    const { login } = useAuthAPI();

    // Real-Time Validation (Efficient: Only Runs on Change)
    useEffect(() => {
        const newValidation = { username: '', password: '' };
        if (username && username.length < 3) {
            newValidation.username = 'Username must be at least 3 characters.';
        }
        if (password && password.length < 6) {
            newValidation.password = 'Password must be at least 6 characters.';
        }
        setValidation(newValidation);
    }, [username, password]);

    const isFormValid = username.length >= 3 && password.length >= 6 && !isLoading;

    // Show Toast Helper
    const showToastMsg = useCallback((msg, type = 'error') => {
        setToastMessage(msg);
        setToastType(type);
        setShowToast(true);
    }, []);

    // Close Toast
    const closeToast = useCallback(() => {
        setShowToast(false);
    }, []);

    // Form Submission Handler (Memoized)
    const handleSubmit = useCallback(async (e) => {
        e.preventDefault();
        if (!isFormValid) {
            showToastMsg('Please fix the errors in the form.');
            return;
        }

        setIsLoading(true);
        const credentials = { username: username.trim(), password };
        const result = await login(credentials);

        setIsLoading(false);

        if (result.success) {
            showToastMsg('Login successful! Redirecting...', 'success');
            setTimeout(() => {
                onLoginSuccess();  // Navigate to Topic Page
            }, 1000);
        } else {
            showToastMsg(result.error);
            setError(result.error);  // Propagate to global error if needed
            usernameRef.current?.focus();  // Refocus on username for retry
        }
    }, [username, password, isLoading, isFormValid, login, showToastMsg, onLoginSuccess, setError]);

    // Handle Enter Key (Keyboard Accessibility)
    const handleKeyDown = useCallback((e) => {
        if (e.key === 'Enter') {
            handleSubmit(e);
        }
    }, [handleSubmit]);

    return (
        <div className="animated-bg">
            <form className="login-form" onSubmit={handleSubmit} aria-labelledby="login-title">
                <h2 id="login-title">File Protector Login</h2>

                {/* Username Field */}
                <div>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        onKeyDown={handleKeyDown}
                        ref={usernameRef}
                        className={username ? (validation.username ? 'invalid' : 'valid') : ''}
                        aria-describedby={validation.username ? 'username-error' : undefined}
                        disabled={isLoading}
                        autoFocus
                        required
                    />
                    {validation.username && (
                        <span id="username-error" className="error-message" role="alert">
                            {validation.username}
                        </span>
                    )}
                </div>

                {/* Password Field */}
                <div>
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        onKeyDown={handleKeyDown}
                        className={password ? (validation.password ? 'invalid' : 'valid') : ''}
                        aria-describedby={password ? 'password-error' : undefined}
                        disabled={isLoading}
                        required
                    />
                    {validation.password && (
                        <span id="password-error" className="error-message" role="alert">
                            {validation.password}
                        </span>
                    )}
                </div>

                {/* Submit Button */}
                <button type="submit" disabled={!isFormValid || isLoading}>
                    {isLoading ? (
                        <>
                            Logging in...
                            <span className="loading-spinner" aria-label="Loading" />
                        </>
                    ) : (
                        'Login'
                    )}
                </button>
            </form>

            {/* Toast Notification */}
            {showToast && (
                <Toast message={toastMessage} type={toastType} onClose={closeToast} />
            )}
        </div>
    );
}

Login.propTypes = {
    onLoginSuccess: PropTypes.func.isRequired,
    setError: PropTypes.func.isRequired,
};

export default React.memo(Login);
